import React from "react";
import {
  collection,
  addDoc,
  doc,
  setDoc,
  getDocs,
  getDoc,
  query,
  orderBy,
  limit,
} from "firebase/firestore";
import { useState, useEffect } from "react";
import { db } from "../firebase";

export default function AdminSection() {
  const [posts, setPosts] = useState([]);
  const [result, setResult] = useState();

  const [title, setTitle] = useState("");
  const [name, setName] = useState("");
  const [party, setParty] = useState("");
  const [votecount, setVotecount] = useState();

  function submithandle() {
    const docRef = doc(db, "Admin", title);

    const colRef = collection(docRef, "candidates");

    setDoc(docRef, {
      sdsd: title,
    });
    addDoc(colRef, {
      Name: name,
      Party: party,
      NoOfVotes: votecount,
    });
  }

  async function publish() {
    const subColRef = query(
      collection(db, "Admin", "mla election", "candidates"),
      orderBy("NoOfVotes", "desc"),
      limit(1)
    );

    const querySnapshot = await getDocs(subColRef);
    querySnapshot.forEach((doc) => {
      // doc.data() is never undefined for query doc snapshots
      console.log(doc.data());
      setResult(doc.data());
    });

    console.log("winner is " + result.Name);
  }

  return (
    <div className="admin">
      <nav className="container mx-auto">
        <h1 className="text-4xl ">Admin Panel</h1>
      </nav>
      <h1>Add Election Data</h1>

      <input
        onChange={(e) => setTitle(e.target.value)}
        type="text"
        placeholder="election name"
      />

      <input
        onChange={(e) => setName(e.target.value)}
        type="text"
        placeholder="candiate name"
      />
      <input
        onChange={(e) => setParty(e.target.value)}
        type="text"
        placeholder="candiate party"
      />
      <input
        onChange={(e) => setVotecount(e.target.value)}
        type="text"
        placeholder="vote count"
      />
      <button type="submit" onClick={submithandle}>
        upload Candidate info
      </button>

      <button type="submit" onClick={publish}>
        Publish Result
      </button>
    </div>
  );
}
