import React from "react";
import {
  collection,
  addDoc,
  doc,
  setDoc,
  getDocs,
  getDoc,
  query,
  orderBy,
  limit,
} from "firebase/firestore";
import { useState, useEffect } from "react";

import { db } from "../firebase";
import { Link } from "react-router-dom";

export default function AdminSection() {
  const [posts, setPosts] = useState([]);
  const [result, setResult] = useState();

  const [title, setTitle] = useState("");
  const [name, setName] = useState("");
  const [party, setParty] = useState("");

  function submithandle() {
    const docRef = doc(db, "Admin", title);

    const colRef = collection(docRef, "candidates");

    setDoc(docRef, {
      sdsd: title,
    });
    addDoc(colRef, {
      Name: name,
      Party: party,
      NoOfVotes: 0,
    });
  }

  async function publish() {
    const subColRef = query(
      collection(db, "Admin", "mla election", "candidates"),
      orderBy("NoOfVotes", "desc"),
      limit(1)
    );

    const querySnapshot = await getDocs(subColRef);
    querySnapshot.forEach((doc) => {
      // doc.data() is never undefined for query doc snapshots
      console.log(doc.data());
      setResult(doc.data());
    });

    console.log("winner is " + result.Name);
  }

  return (
    <div className="admin">
      <nav className="container mt-4 mx-auto flex justify-between">
        <h1 className="text-4xl ">Admin Panel</h1>
        <div className="flex  gap-5">
          <Link to="/adminhome">Admin Home</Link>
          <Link to="/publish">Publish result</Link>
        </div>
      </nav>

      <div className="container mx-auto flex flex-col">
        <div className="mt-8">
          <h1 className="text-2xl">Add Election Data</h1>
        </div>
        <div className="mt-10">
          <input
            onChange={(e) => setTitle(e.target.value)}
            type="text"
            placeholder="election name"
          />

          <input
            onChange={(e) => setName(e.target.value)}
            type="text"
            placeholder="candiate name"
          />
          <input
            onChange={(e) => setParty(e.target.value)}
            type="text"
            placeholder="candiate party"
          />
        </div>
        <div className="mt-10 flex gap-5">
          <button
            className="btn bg-yellow-400 p-2"
            type="submit"
            onClick={submithandle}
          >
            upload Candidate info
          </button>

          <button
            className="btn bg-green-400 p-2"
            type="submit"
            onClick={publish}
          >
            Publish Result
          </button>
        </div>
      </div>
    </div>
  );
}
