import React from "react";
import { collection, addDoc, doc, setDoc, getDocs } from "firebase/firestore";
import { useState, useEffect } from "react";
import { db } from "../firebase";

export default function AdminSection() {
  const [title, setTitle] = useState("");
  const [name, setName] = useState("");
  const [party, setParty] = useState("");

  function submithandle() {
    const docRef = doc(db, "Admin", title);

    const colRef = collection(docRef, "candidates");

    setDoc(docRef, {
      sdsd: title,
    });
    addDoc(colRef, {
      Name: "DSdsd",
      Party: "ddsdsdsd",
    });
  }

  function addcandidate() {
    this.displayData.push(
      <div id="display-data">
        <pre>{this.state.postVal}</pre>
      </div>
    );
    this.setState({
      showdata: this.displayData,
      postVal: "",
    });
  }
  return (
    <div className="admin">
      <h1>Add Election Data</h1>

      <input
        onChange={(e) => setTitle(e.target.value)}
        type="text"
        placeholder="election name"
      />

      <input
        onChange={(e) => setName(e.target.value)}
        type="text"
        placeholder="candiate name"
      />
      <input
        onChange={(e) => setParty(e.target.value)}
        type="text"
        placeholder="candiate party"
      />
      <button type="submit" onClick={submithandle}>
        upload Candidate info
      </button>
      <button type="submit" onClick={addcandidate}>
        add candidate
      </button>
    </div>
  );
}
